def merge_dicts(dict1, dict2):
    dict3 = {}
    for k, v in dict1.items():
        if k in dict2:
            if isinstance(v, dict):
                dict3[k] = merge_dicts(dict1[k], dict2[k])
            else:
                dict3[k] = dict2[k]
        else:
            dict3[k] = dict1[k]
    for k, v in dict2.items():
        if k not in dict3:
            dict3[k] = v
    return deepcopy(dict3)


